mapper.select:
SELECT `at`.id, `at`.type, `at`.money, `at`.systemic, COUNT( a.type_id ) AS notPay, IF(COUNT(a.id)=0 AND systemic=false ,"true","false") AS canDelete FROM tb_amect_type `at` LEFT JOIN tb_amect a ON `at`.id = a.type_id AND a.`status` = 1 WHERE 1=1
AND `at`.type LIKE '%${type}%'
GROUP BY `at`.id LIMIT #{start}, #{length}

mapper.update:
UPDATE tb_amect_type
SET type = #{type},
    money = #{money}
WHERE id = #{id}

mapper.delete:
DELETE FROM tb_amect_type
WHERE systemic = false  AND id IN

    #{one}
